import '../src/scss/main.scss';

function scrollTo(y) {
   //return window.scrollTo(0, y);
  console.log(specialistics.getBoundingClientR);
   //specialistics
}

function getCoords(elem) {
    let box = elem.getBoundingClientRect();

    return {
      top: box.top + box.pageYOffset,
      left: box.left + box.pageXOffset
    };
  }
const specialistics = document.getElementById('specialistics');
console.log(specialistics);

const scrollToSpecialistics = document.getElementById('scrollToSpecialistics');
console.log(scrollToSpecialistics);

scrollToSpecialistics.addEventListener('click', () => getCoords(specialistics))
